import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable()
export class UserService {

  constructor(private http: HttpClient) { }

  loadUsers(filter?: { [key: string]: string }) {

    const filterString = filter
      ? '?' + Object.keys(filter).map(key => `${key}=${filter[key]}`).join('&')
      : '';

    return this.http.get<any[]>(`https://jsonplaceholder.typicode.com/users${filterString}`);
  }

  loadUser(id: number) {
    return this.http.get<any>(`https://jsonplaceholder.typicode.com/users/${id}`);
  }
}
